package Switchh;

public class Program1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int choice=2;
		switch(choice)
		{
		case 1:System.out.println("From Choice1");
		break;
		case 2:System.out.println("From Choice2");
		break;
		case 3:System.out.println("From Choice3");
		break;
		default:System.out.println("Invalid");
		}
		
		}

	}


